mapper.delete:
/**
* Query ID : deleteAreaInfo
* Description : 공장 제거
* Author : kct
**/

	DELETE 
	FROM AREA_INFO
	WHERE 1=1



		AND iCityNum = #{iCityNum}




		AND iAreaNum = #{iAreaNum}

mapper.select:
/** * Query ID : measureStatusList * Description : 시간별 공장 발전량 리스트 * Author : kct **/
SELECT A.Status_Board_State AS tdStatusBoardState FROM STX_SOFC_MEASURE A JOIN ( SELECT RTU_ID, MAX(TIMESTAMP) AS TIME FROM STX_SOFC_MEASURE WHERE RTU_ID IN ( SELECT iRtuNum FROM SYSTEM_INFO WHERE 1=1
AND iCityNum = #{iCityNum}
AND iAreaNum = #{iAreaNum}
AND iSiteNum = #{iSiteNum}
) AND TIMESTAMP BETWEEN CONVERT(DATE, GETDATE()) AND DATEADD(DAY, 1, CONVERT(DATE, GETDATE())) GROUP BY RTU_ID ) B ON A.RTU_ID = B.RTU_ID AND A.TIMESTAMP = B.TIME

/**
* Query ID : systemCnt
* Description : 공장 리스트 갯수 조회
* Author : kct
**/

	SELECT COUNT(*) AS cnt
	FROM SYSTEM_INFO A
	WHERE 1=1



			AND A.iCityNum = #{iCityNum}




			AND A.iAreaNum = #{iAreaNum}




			AND A.iSiteNum = #{iSiteNum}




			AND A.iRtuNum = #{iRtuNum}

/**
* Query ID : systemRtuIdList
* Description : 공장 번호 리스트 조회
* Author : kct
**/

	SELECT A.sSystemName, A.iRtuNum
	FROM SYSTEM_INFO A
	WHERE 1=1



			AND A.iCityNum = #{iCityNum}




			AND A.iAreaNum = #{iAreaNum}




			AND A.iSiteNum = #{iSiteNum}
